import React from "react";
import PopupWithForm from "./PopupWithForm";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import TextField from "./TextField";
import UrlField from "./UrlField";

function EditProfilePopup(props) {
  const [name, setName] = React.useState("");
  const [description, setDescription] = React.useState("");
  const currentUser = React.useContext(CurrentUserContext);

  function handleNameChange(evt) {
    setName(evt.target.value);
  }

  function handleDescriptionChange(evt) {
    setDescription(evt.target.value);
  }

  function handleSubmit(evt) {
    evt.preventDefault();

    props.onSubmit({
      profile_name: name,
      profile_job: description,
    });
  }

  React.useEffect(() => {
    if (props.isOpen) {
      setName(currentUser.name);
      setDescription(currentUser.about);
    }
  }, [props.isOpen, currentUser]);

  return (
    <>
      <PopupWithForm
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
        id={"popup-edit"}
        form={"profiel-edit"}
        title={"Редактировать профиль"}
        buttonText={"Сохранить"}
        children={
          <>
            <div className="popup__field">
              <input
                id="popup__edit-name-input"
                className="popup__input"
                type="text"
                minLength="2"
                maxLength="40"
                name="name"
                placeholder="Имя"
                required=""
              />
              <span className="popup__edit-name-input-error popup__input-error" />
            </div>
            <div className="popup__field">
              <input
                id="popup__edit-about-input"
                className="popup__input"
                type="text"
                minLength="2"
                maxLength="200"
                name="about"
                placeholder="О себе"
                required=""
              />
              <span className="popup__edit-about-input-error popup__input-error" />
            </div>
          </>
        }
      />

      <PopupWithForm
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
        id={"popup-add"}
        form={"card-create"}
        title={"Новое место"}
        buttonText={"Создать"}
        children={
          <>
            <div className="popup__field">
              <input
                id="popup__add-name-input"
                className="popup__input"
                type="text"
                minLength="2"
                maxLength="30"
                name="name"
                placeholder="Название"
                required=""
              />
              <span className="popup__add-name-input-error popup__input-error" />
            </div>
            <div className="popup__field">
              <input
                id="popup__add-link-input"
                className="popup__input"
                type="url"
                name="link"
                placeholder="Ссылка на картинку"
                required=""
              />
              <span className="popup__add-link-input-error popup__input-error" />
            </div>
          </>
        }
      />
    </>
  );
}

export default EditProfilePopup;
