import React from 'react';
import api from '../utils/Api';


function Main(props) {
    const [userName, setUserName] = React.useState();
    const [userDescription, setUserDescription] = React.useState();
    const [userAvatar, setUserAvatar] = React.useState();
    const [cards, setCards] = React.useState([]);
    
    React.useEffect(() => {
        api.getUser().getInitialCards().then((userData, initialCardsData) => {
            setUserName(userData.name);
            setUserDescription(userData.about);
            setUserAvatar(userData.avatar);
            setCards(initialCardsData.name);
            setCards(initialCardsData.link);
        }).catch((err) => {
            console.log(err);
        });
        
    }, [])

    // React.useEffect(() => {
    //     api.getInitialCards().then((initialCardsData) => {
    //         setCards(initialCardsData.name);
    //         setCards(initialCardsData.link);
    //     }).catch((err) => {
    //         console.log(err);
    //     });
    // }, [])



    return (
        <main className="content">
            <section className="profile">
                <div className="profile__container">
                    <div className="profile__img-mask">
                        <button
                            className="profile__avatar-edit"
                            type="button"
                            title="Обновить аватар"
                            onClick={props.onEditAvatar}
                        />
                        <img
                            className="profile__avatar"
                            alt="Аватар"
                            src={userAvatar}
                        />
                    </div>
                    <div className="profile__info">
                        <div className="profile__info-container">
                            <h1 className="profile__name">
                                <span className="profile__span">{userName}</span>
                            </h1>
                            <button
                                className="profile__edit-button"
                                type="button"
                                title="Редактировать профиль"
                                onClick={props.onEditProfile}
                            />
                        </div>
                        <p className="profile__about">{userDescription}</p>
                    </div>
                </div>
                <button
                    className="profile__add-button"
                    type="button"
                    title="Добавить фотографию"
                    onClick={props.onAddPlace}
                />
            </section>
            <section className="elements">
                <ul className="elements__list" />
            </section>
        </main>
    )
}

export default Main;
