import React from "react";
import PopupWithForm from "./PopupWithForm";
import { CurrentUserContext } from "../contexts/CurrentUserContext";

function AddPlacePopup(props) {
  const currentUser = React.useContext(CurrentUserContext);
  const [name, setName] = React.useState("");
  const [link, setLink] = React.useState("");
  const initialState = "Создать";
  const [buttonText, setButtonText] = React.useState(initialState);

  function changeButton() {
    setButtonText("Сохранение...");
    setTimeout(() => setButtonText(initialState), [1000])
  }

  React.useEffect(() => {
    if (props.isOpen) {
      setName(currentUser.name);
      setLink(currentUser.link);
    }
  }, [props.isOpen, currentUser]);

  function handleName(evt) {
    setName(evt.target.value);
  }

  function handleLink(evt) {
    setLink(evt.target.value);
  }

  function handleSubmit(evt) {
    evt.preventDefault();
    evt.target.reset();

    props.onUpdateUser({
      name,
      link: link,
    });
  }

  return (
    <PopupWithForm
      isOpen={props.isOpen}
      onClose={props.onClose}
      onClick={changeButton}
      id={"popup-add"}
      form={"card-create"}
      title={"Новое место"}
      buttonText={buttonText}
      onSubmit={handleSubmit}
    >
      <div className="popup__field">
        <input
          id="popup__add-name-input"
          className="popup__input"
          type="text"
          minLength="2"
          maxLength="30"
          name="name"
          placeholder="Название"
          onChange={handleName}
          required
        />
        <span className="popup__add-name-input-error popup__input-error" />
      </div>
      <div className="popup__field">
        <input
          id="popup__add-link-input"
          className="popup__input"
          type="url"
          name="link"
          placeholder="Ссылка на картинку"
          onChange={handleLink}
          required
        />
        <span className="popup__add-link-input-error popup__input-error" />
      </div>
    </PopupWithForm>
  );
}

export default AddPlacePopup;
