function handleEditAvatarClick(evt) {
    evt.target.addEventListener('click', () => {
        document.querySelector('#popup-add-avatar').classList.add('popup_opened')
    });
}


function handleEditProfileClick(evt) {
    evt.target.addEventListener('click', () => {
        document.querySelector('#popup-edit').classList.add('popup_opened')
    });

}


function handleAddPlaceClick(evt) {
    evt.target.addEventListener('click', () => {
        document.querySelector('#popup-add').classList.add('popup_opened')
    });
}


function Main() {
    return (
        <main className="content">
            <section className="profile">
                <div className="profile__container">
                    <div className="profile__img-mask">
                        <button
                            onClick={handleEditAvatarClick}
                            className="profile__avatar-edit"
                            type="button"
                            title="Обновить аватар"
                        />
                        <img
                            className="profile__avatar"
                            alt="Аватар"
                            src="<%=require('./images/profiel/avatar.png')%>"
                        />
                    </div>
                    <div className="profile__info">
                        <div className="profile__info-container">
                            <h1 className="profile__name">
                                <span className="profile__span">Жак-Ив Кусто</span>
                            </h1>
                            <button
                                onClick={handleEditProfileClick}
                                className="profile__edit-button"
                                type="button"
                                title="Редактировать профиль"
                            />
                        </div>
                        <p className="profile__about">Исследователь океана</p>
                    </div>
                </div>
                <button
                    onClick={handleAddPlaceClick}
                    className="profile__add-button"
                    type="button"
                    title="Добавить фотографию"
                />
            </section>
            <section className="elements">
                <ul className="elements__list" />
            </section>
        </main>
    )
}

export default Main;
